import { useState, useEffect } from 'react'
import { Button, Form } from 'react-bootstrap'

const AddComment = ({asin}) => {


    const [comment, setComment] =useState({
        comment: '',
        rate: 1,
        elementId: null
    })

   
    useEffect(() => {
        setComment(comment =>({
            ...comment,
            elementId: asin
        }))

    },[asin])
  

   const addCommentHere = async (e) => {
        e.preventDefault()
        try {
            let response = await fetch('https://striveschool-api.herokuapp.com/api/comments', {
                method: 'POST',
                body: JSON.stringify(comment),
                headers: {
                    'Content-type': 'application/json',
                    Authorization: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MTFkMGE0ODJkNTI2MjAwMTViNmRjZjkiLCJpYXQiOjE2MzAzOTAxNDAsImV4cCI6MTYzMTU5OTc0MH0.QbD1uetM-lJWrCAABoomjYVwLZsOFZ-XsZ2cytde9DA'
                }
            })
            if (response.ok) {
                alert('Comment was successfully sent!')
                setComment({
                    comment: '',
                    rate: 1,
                    elementId: null
                })
            } else {
                console.log('error')
                alert('an error is occured')
            }
        } catch (error) {
            console.log('error')
        }
    }

 
        return (
            <div>
                <Form onSubmit={addCommentHere}>
                    <Form.Group>
                        <Form.Label>Comment text</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="So what do you like to say"
                            value={comment.comment}
                            onChange={e => setComment({
                                
                                    ...comment,
                                    comment: e.target.value
                               
                            })}
                        />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Rating</Form.Label>
                        <Form.Control as="select" value={comment.rate}
                            onChange={e => setComment({
                                    ...comment,
                                    rate: e.target.value
                                
                            })}>
                            <option>1</option>
                            <option>2</option>
                            <option>3</option>
                            <option>4</option>
                            <option>5</option>
                        </Form.Control>
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>
            </div>
        )
    
}

export default AddComment
